def parallel_circuit(*resistors, voltage):
    R_total = 1 / sum(1/r for r in resistors)
    currents = [voltage / r for r in resistors]
    I_total = sum(currents)
    return {
        'R_total': round(R_total, 3),
        'I_total': round(I_total, 3),
        'branch_currents': [round(i, 3) for i in currents],
        'branch_power': [voltage * i for i in currents]
    }

# Two resistor shortcut
def two_parallel(R1, R2):
    return (R1 * R2) / (R1 + R2)

# Example
result = parallel_circuit(6, 12, voltage=12)
