def kcl_node(currents_in, currents_out):
    """Verify KCL at a node"""
    total_in = sum(currents_in)
    total_out = sum(currents_out)

    return {
        'total_in': total_in,
        'total_out': total_out,
        'kcl_verified': abs(total_in - total_out) < 1e-9,
        'imbalance': total_in - total_out
    }

def find_unknown_current(known_in, known_out):
    """Find missing current at a node"""
    return sum(known_in) - sum(known_out)

# Example: 5A in, 2A out, find third current
unknown = find_unknown_current([5], [2])  # 3A out

class TheveninNorton:
    def __init__(self, V_oc=None, I_sc=None, R_eq=None):
        if V_oc and I_sc:
            self.V_th = V_oc
            self.R_th = V_oc / I_sc
            self.I_n = I_sc
        elif V_oc and R_eq:
            self.V_th = V_oc
            self.R_th = R_eq
            self.I_n = V_oc / R_eq

    def load_current(self, R_load):
        return self.V_th / (self.R_th + R_load)

    def load_voltage(self, R_load):
        I = self.load_current(R_load)
        return I * R_load

    def max_power_load(self):
        """R_load for maximum power transfer"""
        return self.R_th

    def max_power(self):
        R_load = self.R_th
        I = self.V_th / (2 * self.R_th)
        return I**2 * R_load

# Example
circ = TheveninNorton(V_oc=12, R_eq=6)
I_load = circ.load_current(3)  # 1.33A
