// Intrinsic carrier concentration
function intrinsicCarriers(Nc, Nv, Eg, T) {
  const k = 8.617e-5;  // eV/K
  return Math.sqrt(Nc * Nv) * Math.exp(-Eg / (2 * k * T));
}

const ni = intrinsicCarriers(2.8e19, 1.04e19, 1.12, 300);
console.log(`ni = ${ni.toExponential(2)} /cm¬≥`);

