// Temperature sensor using semiconductor properties
const int thermistorPin = A0;
const float BETA = 3950;  // Thermistor constant

void setup() {
  Serial.begin(9600);
}

void loop() {
  int reading = analogRead(thermistorPin);
  float resistance = 10000.0 * reading / (1023 - reading);
  float tempK = 1.0 / (1.0/298.15 + log(resistance/10000.0)/BETA);
  Serial.println(tempK - 273.15);
  delay(1000);
}

