// Diode current calculator
function diodeCurrent(V, Is = 1e-12, n = 1, T = 300) {
  const Vt = 0.0259;  // Thermal voltage
  return Is * (Math.exp(V / (n * Vt)) - 1);
}

const I = diodeCurrent(0.7);
console.log(`Current at 0.7V: ${(I * 1000).toFixed(2)} mA`);

