// LED brightness control with PWM
const int ledPin = 9;
const int potPin = A0;

void setup() {
  pinMode(ledPin, OUTPUT);
}

void loop() {
  int sensorValue = analogRead(potPin);
  int brightness = map(sensorValue, 0, 1023, 0, 255);
  analogWrite(ledPin, brightness);
  delay(15);
}

codeExample.javascript:
// BJT operating point calculator
function bjtOperatingPoint(Vcc, Rc, Rb, beta, Vbe = 0.7) {
  const Ib = (Vcc - Vbe) / Rb;
  const Ic = beta * Ib;
  const Vc = Vcc - Ic * Rc;
  return { Ib, Ic, Vc };
}

const result = bjtOperatingPoint(12, 1000, 100000, 200);
console.log(`Ic = ${(result.Ic * 1000).toFixed(1)} mA`);

