// Transistor as a switch to control motor
const int buttonPin = 2;
const int transistorPin = 9;

void setup() {
  pinMode(buttonPin, INPUT_PULLUP);
  pinMode(transistorPin, OUTPUT);
}

void loop() {
  if (digitalRead(buttonPin) == LOW) {
    digitalWrite(transistorPin, HIGH);  // Turn on motor
  } else {
    digitalWrite(transistorPin, LOW);   // Turn off motor
  }
}

codeExample.javascript:
// Op-amp gain calculator
function opAmpGain(type, Rf, Rin) {
  if (type === 'inverting') {
    return -Rf / Rin;
  } else {
    return 1 + Rf / Rin;
  }
}

const gain = opAmpGain('non-inverting', 100000, 10000);
console.log(`Gain = ${gain}x`);

