// Reading sensor with op-amp signal conditioning
const int sensorPin = A0;
const float gain = 11.0;  // 1 + 100k/10k
const float Vref = 5.0;

void setup() {
  Serial.begin(9600);
}

void loop() {
  int raw = analogRead(sensorPin);
  float Vout = (raw / 1023.0) * Vref;
  float Vsensor = Vout / gain;  // Calculate original sensor voltage
  Serial.print("Sensor: ");
  Serial.println(Vsensor, 3);
  delay(500);
}
