# Resistor color code decoder
color_values = {
    'black': 0, 'brown': 1, 'red': 2, 'orange': 3,
    'yellow': 4, 'green': 5, 'blue': 6, 'violet': 7,
    'gray': 8, 'white': 9
}
multipliers = {'black': 1, 'brown': 10, 'red': 100,
               'orange': 1000, 'yellow': 10000}

def decode_resistor(band1, band2, multiplier):
    value = (color_values[band1] * 10 + color_values[band2])
    return value * multipliers[multiplier]

# Brown-Black-Red = 1000Î© = 1kÎ©
resistance = decode_resistor('brown', 'black', 'red')

# Ohm's Law Calculator
class OhmsLaw:
    @staticmethod
    def voltage(I, R):
        """V = I Ã— R"""
        return I * R

    @staticmethod
    def current(V, R):
        """I = V / R"""
        return V / R

    @staticmethod
    def resistance(V, I):
        """R = V / I"""
        return V / I

    @staticmethod
    def led_resistor(supply_v, led_v, led_ma):
        """Calculate LED current limiting resistor"""
        v_drop = supply_v - led_v
        current = led_ma / 1000  # mA to A
        return v_drop / current

# Example: 5V supply, 2V LED, 20mA
R = OhmsLaw.led_resistor(5, 2, 20)  # 150Î©

# Power calculations
class PowerCalc:
    @staticmethod
    def power_vi(V, I):
        return V * I

    @staticmethod
    def power_i2r(I, R):
        return I**2 * R

    @staticmethod
    def power_v2r(V, R):
        return V**2 / R

    @staticmethod
    def energy_kwh(power_watts, hours):
        return (power_watts * hours) / 1000

    @staticmethod
    def electricity_cost(kwh, rate_per_kwh):
        return kwh * rate_per_kwh

# Example: 1500W heater for 3 hours at $0.12/kWh
energy = PowerCalc.energy_kwh(1500, 3)  # 4.5 kWh
cost = PowerCalc.electricity_cost(4.5, 0.12)  # $0.54
