codeExample.javascript:
// Cloudflare Worker: A/B Testing at Edge
addEventListener('fetch', event => {
  event.respondWith(handleRequest(event.request))
})

async function handleRequest(request) {
  // Get or assign user to test group
  const cookie = request.headers.get('Cookie') || ''
  let testGroup = getCookie(cookie, 'ab_test')

  if (!testGroup) {
    testGroup = Math.random() < 0.5 ? 'A' : 'B'
  }

  // Modify origin URL based on test group
  const url = new URL(request.url)
  if (testGroup === 'B') {
    url.pathname = '/variant-b' + url.pathname
  }

  const response = await fetch(url.toString(), request)
  const newResponse = new Response(response.body, response)

  // Set cookie for consistent experience
  newResponse.headers.set('Set-Cookie', 
    `ab_test=${testGroup}; Path=/; Max-Age=86400`)

  return newResponse
}
